INSERT INTO `config_info` VALUES (657, 'application-dev.yml', 'DEFAULT_GROUP', 'spring:\n  # jackson序列化\n  jackson:\n    # 使用jackson序列化时排除null字段\n    default-property-inclusion: non_null\n  # redis缓存\n  redis:\n    host: network-yuli-redis\n    port: 6379\n    database: 1\n  # zipkin\n  zipkin:\n    # server地址\n    base-url: http://127.0.0.1:9411/\n    # 让nacos当成一个url解析\n    discoverClientEnabled: false\n  seluth:\n    sampler:\n      # 采样百分比\n      probability: 1.0\n\n# feign配置\nfeign:\n  sentinel:\n    enabled: true\n  okhttp:\n    enabled: true\n  httpclient:\n    enabled: false\n  client:\n    config:\n      default:\n        connectTimeout: 5000\n        readTimeout: 5000\n\nribbon:\n  ReadTimeout: 5000\n  ConnectTimeout: 5000\n\nmybatis-plus:\n  mapper-locations: classpath:/mapper/*Mapper.xml\n  configuration:\n    #开启sql日志\n    log-impl: org.apache.ibatis.logging.stdout.StdOutImpl\n\n# 加密根密码\njasypt:\n  encryptor:\n    password: yuli\n\n# security通用配置\nsecurity:\n  oauth2:\n    resource:\n      loadBalanced: true\n      token-info-uri: http://yuli-auth/oauth/check_token\n    # 通用放行URL，服务个性化，请在对应配置文件覆盖\n    ignore:\n      urls:\n        - /v2/api-docs\n        - /actuator/**\n\n# swagger配置\nswagger:\n  title: YULI CLOUD Swagger API\n  license: Powered By hanguilin\n  licenseUrl:\n  terms-of-service-url:\n  contact:\n    email: 1451633962@qq.com\n    url: http://docs.javayuli.cn\n  authorization:\n    name: YULI CLOUD OAuth\n    auth-regex: ^.*$\n    authorization-scope-list:\n      - scope: server\n        description: server all\n    token-url-list:\n      - http://network-yuli-getway:8000/auth/oauth/token', '7c32353f4f81a2e8e8688541681f1b25', '2020-12-27 06:51:49', '2021-01-31 04:05:22', NULL, '0:0:0:0:0:0:0:1', '', '', '', '', '', 'yaml', '');
INSERT INTO `config_info` VALUES (661, 'yuli-gateway-dev.yml', 'DEFAULT_GROUP', 'server:\n  port: 8000\nspring:\n  cloud:\n    sentinel:\n      transport:\n        dashboard: network-yuli-sentinel:7000\n    gateway:\n      routes:\n      - id: yuli-auth\n        uri: lb://yuli-auth\n        predicates:\n        - Path=/auth/**\n        filters:\n        - RewritePath=/auth/(?<segment>.*), /$\\{segment}\n      - id: yuli-system-api\n        uri: lb://yuli-system-api\n        predicates:\n        - Path=/sys/**\n        filters:\n        - RewritePath=/sys/(?<segment>.*), /$\\{segment}\n      - id: yuli-code-generator\n        uri: lb://yuli-code-generator\n        predicates:\n        - Path=/codegen/**\n        filters:\n        - RewritePath=/codegen/(?<segment>.*), /$\\{segment}\n      - id: yuli-quartz\n        uri: lb://yuli-quartz\n        predicates:\n        - Path=/quartz/**\n        filters:\n        - RewritePath=/quartz/(?<segment>.*), /$\\{segment}', '9a98df43f54bece65e424f20688c79f9', '2020-12-27 06:55:50', '2021-01-28 05:56:25', NULL, '0:0:0:0:0:0:0:1', '', '', '', '', '', 'yaml', '');
